export const getMockTableData = async (str) =>
{
  let result=null
  switch (str) {
    case '/branch/1':
      result = tableBranch1
      break
    case '/branch/2':
      result = tableBranch2
      break
    case '/branch/3':
      result =  tableBranch3
      break
    default:
      break
    }
    if(result)
    {
      return new Promise((resolve, reject) =>{
        resolve(result)
        //reject('error')
      })
    }
    else
    {
      return new Promise((resolve, reject) =>{
        //resolve(result)
        reject('api tables error')
      })
    }
}

