import React, { Component } from 'react';
import TreeNode from './TreeNode';
import {connect} from 'react-redux'
import {getRootTreeElements} from 'selectors'
import {fetchInitialTreeData} from 'actions'

class TreeView extends Component {
  componentDidMount()
  {
    this.props.fetchInitialTreeData()
  }

  getRootNodes = () => {
    const {root} = this.props
    return root
  }

  convertRootNode = node => {
    return(
      <TreeNode
        key={node.id}
        node={node}
      />
    )
  }

  render() {
    const rootNodes = this.getRootNodes();
    return rootNodes.map(node => {return this.convertRootNode(node)})
  }
}

const mapStateToProps = (state, ownProps) => ({
  root: getRootTreeElements(state)
})
const mapDispatchProps={
  fetchInitialTreeData
}

export default connect(mapStateToProps, mapDispatchProps)(TreeView)
